callback_with_context.context_function_id:
( target_mission = PlayersFaction.ActiveMissionList.Filter((mission) => HasZoomLocation && (TargetCharacterContext == this)).FirstContext ) =>
{
 GetIfElse( CanUseUiFeature("WRATH_OF_KHORNE") && IsValidContext(target_mission) && target_mission.MissionRecordContext.EventCategoryContext.CategoryKey == "Arbaal_map_easy" || target_mission.MissionRecordContext.EventCategoryContext.CategoryKey == "Arbaal_map_medium" || target_mission.MissionRecordContext.EventCategoryContext.CategoryKey == "Arbaal_map_hard", true, false )
}
