callback_with_context.context_function_id:
(initiatives = GetIfElse(Component("6E24E192-CB18-4BC2-88C00EF016E8F2EA").CurrentSelection.SelectedObjectList.FirstContext == this,
  Component("D4ABC27F-36DE-4E8B-ABD4B44C4E9AF716").CurrentSelection.SelectedObjectList.Sort(InitiativeContext.UpkeepResourceTransactionContext.PooledResourceTransactionList.FirstContext.Value, true).Partition(IsActive),
  InitiativeList.Filter(IsActive).Sort(InitiativeContext.UpkeepResourceTransactionContext.PooledResourceTransactionList.FirstContext.Value, true)),
  max_initiatives = this.ActiveInitiativeLimit) => {

  initiatives + GetIf(initiatives.Size < max_initiatives, Generate(max_initiatives - initiatives.Size))

