callback_with_context.context_function_id:
( characters = ActiveMissionList.Filter((mission) => HasZoomLocation && IsContextValid(TargetCharacterContext)).Transform((mission) => mission.TargetCharacterContext) ) =>
{
 GetIf( characters.IsEmpty == false, characters.Filter(IsVisible && ShouldShowOnRadar).Transform((character) => character.MilitaryForceContext) )
}
